  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = (credentials) => {
    if (credentials.username === 'admin' && credentials.password === 'admin') {
      setIsLoggedIn(true);
    }
  }
  return (
    <div className="App">
      <Header />
      <nav className="navbar">

      </nav>
      <div className="content-wrapper">
        <Chessboard />
      </div>
    </div>
  );
}

export default App;
